INSERT INTO `course` VALUES ('1003860002', '1003860002', '0', 'iOS入门视频-Objective-C语言入门', '华清创客学院', '4.4', '-1', '252', '4', '郭老师', '0', null, null, 'http://img0.ph.126.net/83Y0cjDUKwXFPRVfHdLwdQ==/6632014445213459934.jpg', 'http://img0.ph.126.net/FQhCahn5BAvM-gBz6hc53w==/6632442155233995483.jpg', '环境搭建及面向对象基础\n\nOC语言是一门面向对象的编程语言，应用在iOS编程上。OC语言的设计目标是以面向过程的C语言作为基础抽象出的高级编程语言！学习OC需要抽象过程，树立面向对象的思想，你准备好了吗？\n\n课堂有什么问题欢迎加群309298691交流\n\n想要学习更多ios开发请关注创客学院http://www.makeru.com.cn/');
