INSERT INTO `course` VALUES ('1210612807', '1210612807', '0', '全国计算机二级C语言精讲教程', '励程教育', '4.4', '-1', '4', '40', null, '59', null, null, 'https://edu-image.nosdn.127.net/ea88180f8e35408e8e24a7f3ddbe6c2d.jpg?imageView&quality=100&crop=0_0_1080_608', 'https://edu-image.nosdn.127.net/ea88180f8e35408e8e24a7f3ddbe6c2d.jpg?imageView&quality=100&crop=0_0_1080_608', '课程简介\n零基础入门精修C语言，结合全国计算机等级考试真题讲解，让学员轻松入门C语言，应对计算机二级考试。\n\n适合人群\n适合零基础或基础薄弱的学员，不知道如何备考，想短期通过二级C语言考试的学生。\n\n学习方式\n系统学完本套课程，同时结合历届计算机等级考试真题练习，助力轻松过二级\n\n课程亮点：\n结合计算机二级考试题进行讲解分析。');
