INSERT INTO `course` VALUES ('1320004', '1320004', '0', 'C语言基础入门与提高', '我赢职场', '4.5', '2', '5844', '32', '周清', '0', null, null, 'http://img0.ph.126.net/7OccLytIgcw42mRhiZkzjQ==/6631248085606249653.jpg', 'http://img1.ph.126.net/qOBeMsy0FZHWt06kCcDzxw==/6631248085606249651.jpg', 'C语言基础入门与提高，主要在mac系统、Xcode工具环境下编写C语言代码，从零基础由浅入深讲解C语言的数据类型、流程控制语句、数组、函数，指针、字符串、预处理、文件操作等知识点，结合大量案例，如：彩票随机21选5系统、成绩管理系统，斗地主发牌器，通讯录项目，通过这些项目实战让我们的零基础学员理论结合实践，打好C语言的基础。');
INSERT INTO `course` VALUES ('1321006', '1321006', '0', 'C语言：斗地主的发牌器', '我赢职场', '4.3', '1', '2662', '4', '周清', '0', null, null, 'http://img1.ph.126.net/-TpedA8LUnckFoWr1cE4FQ==/6630505915258405846.jpg', 'http://img2.ph.126.net/SIpSwijlGIIr4XOC9ekmTg==/6631337146048078799.jpg', '斗地主的发牌系统是一款用C语言编写的发牌系统，通过本项目的学习，可以让学员掌握一些棋牌类的手机游戏中的发牌系统算法怎么编写，为学员就业后编写一些小的游戏类APP打下良好的基础。');
