INSERT INTO `course` VALUES ('636002', '636002', '0', '零基础C语言学习教程 C语言程序设计', 'geek99', '4.6', '2', '34690', '30', null, '0', null, null, 'http://img1.ph.126.net/1C_wRuSZT-qdOPHNTreTKg==/6608825744980268179.jpg', 'http://img2.ph.126.net/R1LkgyQLteR8b1UKQ5cu2A==/4850095323801698069.jpg', '【课程简介】\n本课程是c语言入门课程，课程分为16章，详细讲述了c语言开发的各个方面，从开发环境搭建、Hello C到指针、数组、结构体、文件操作都有详细讲解。\n【极客在线教育】\n极客在线教育http://www.geek99.com，专注于移动互联网课程的开发与培训，课程内容包括C、C++、Java、Objective-C、Android、iOS、Cocos2d-x和HTML5等流行课程。');
